create table music (id integer generated by default as identity (start with 1), library varchar(255), original_title varchar(255), overview varchar(255), poster_path varchar(255), tagline varchar(255), title varchar(255), primary key (id));
create table music_composer (music_id integer not null, composer_id integer not null);
create table music_genres (music_id integer not null, genres_id integer not null);